export function binarySearch(arr: number[], target: number): number {
	let left = 0;
	let right = arr.length - 1;
	while (left <= right) {
		const mid = Math.floor((left + right) / 2);
		const value = arr[mid];
		if (value === target) return mid;
		if (value < target) left = mid + 1;
		else right = mid - 1;
	}
	return -1;
}

export function twoSum(nums: number[], target: number): [number, number] | null {
	const seen = new Map<number, number>();
	for (let i = 0; i < nums.length; i++) {
		const value = nums[i];
		const need = target - value;
		const match = seen.get(need);
		if (match !== undefined) return [match, i];
		seen.set(value, i);
	}
	return null;
}

export type ListNode = { value: number; next: ListNode | null };

export function mergeTwoSortedLists(a: ListNode | null, b: ListNode | null): ListNode | null {
	const dummy: ListNode = { value: 0, next: null };
	let tail = dummy;
	let left = a;
	let right = b;
	while (left !== null && right !== null) {
		if (left.value <= right.value) {
			tail.next = left;
			left = left.next;
		} else {
			tail.next = right;
			right = right.next;
		}
		tail = tail.next;
	}
	tail.next = left ?? right;
	return dummy.next;
}

def fibonacci(n: int) -> int:
	if n <= 0:
		return 0
	if n == 1:
		return 1
	a = 0
	b = 1
	for _ in range(2, n + 1):
		a, b = b, a + b
	return b
